<script>
import tags from "@/api/tags";
import pv from "@/api/webdata";

export default {
  props: {
    //总文章数
    total: Number
  },
  data() {
    return {
      pv: "",
      //标签
      tags: [],
      //日历选中值
      value: "",
      //日历对象
      context: null,
      //简介背景
      src: require("../../../assets/6b733ff292abbd0d6ff4dc90271861c_r2_c1.jpg"),
      // 头像配置
      mainProps: {
        // blank: true,
        // blankColor: "#777",
        width: 75,
        src: require("../../../assets/v2-b0911ea69cb7e0e4d14a8dcbc0ead2a6_r.jpg"),
        height: 75,
        class: "m1"
      }
    };
  },
  methods: {
    onSelected(ymd, date) {
      // console.log(ctx)
      // this.context = ctx;
      // console.log(this.context.activeYMD)
      console.log(ymd, date, typeof ymd);
      this.$emit("tagSearch", null, ymd, "null");
    },
    //点击标签，更新文章列表
    searchTag(tagContent) {
      this.$emit("tagSearch", tagContent, null, "null");
      // alert(tagContent);
    },
    //获取标签
    fetchTagsList() {
      tags.getTags().then(response => {
        const resp = response.data;
        console.log(resp);
        if (resp.flag) {
          this.tags = resp.tags;
        } else {
          console.log("获取标签失败");
        }
      });
    },
    // 获取浏览量
    fetchPV() {
      pv.getPV().then(response => {
        const resp = response.data;
        console.log(resp);
        this.pv = resp.data[0].PV;
      });
    }
  },
  created() {
    this.fetchTagsList();
    this.fetchPV();
  }
};
</script>

